                    <input type="text" class="form-control" id="date"                                                                >                                                            </div>                                                            <label for="inputValue" class="col-md-2 control-label">Remarques</label>                                                            <div class="col-md-2">                                                                <textarea class="form-control" id="remarques"                                                                ></textarea>                                                                <input type="text" class="hidden form-control" id="idV"                                                                >                                                            </div>                                                            <div class="col-md-2">                                                                <a id="modifyVisit"                                                                   class="modifyVisit btn btn-primary btn-outline btn-circle">Modifier</a>                                                            </div>                                                        </div>                                                    </div>                                                </div>                                            </div>                                        </div>                                    </div>                                    <table id="tableVisit" data-toggle="table"                                           data-url="./bDtoAllVisits"                                           data-pagination="false"                                           data-side-pagination="server"                                           data-height="200"                                           data-click-to-select="true"                       