             >                                        <thead>                                        <tr>                                            <th data-field="nom">Titre</th>                                            <th data-field="dateTel">                                                Date d'ajout                                            </th>                                            <th data-field="action" data-align="center"                                                data-formatter="actionFormatterFile" data-events="actionEvents">                                                Operations                                            </th>                                        </tr>                                        </thead>                                    </table>                                </div>                            </div>                            <div class="modal-footer ">                                <ul class="pager wizard">                                    <li class="previous" name="previous"><a href="#"><i                                                class="glyphicon glyphicon-chevron-left"></i>Retour</a></li>                                    <li class="next"><a href="#">Suivant<i                                                class="glyphicon glyphicon-chevron-right"></i></a></li>                                </ul>                                <button type="submit" id="buttonMAJ" name="buttonMAJ" class="btn btn-warning btn-lg"                                        style="width: 100%;"><span class="glyphicon glyphicon-ok-sign"></span>&nbsp;&nbsp;Mettre                                    à jour                                </button>                            </div>                        </div>                    </fieldset>                </div>            </form>        </div>    </div></div><!-- /.modal-content --><!-- /.modal-dialog --><!-- POP Edit Projet 